import React, { useState } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Textform from './components/Textform';
import Alert from './components/Alert'
// import About from './components/About';

function App() {
  const [mode, setMode] = useState('light');
  const [alert, setAlert] = useState(null);
  const showAlert = (message, type)=>{
    setAlert({
      message:message,
      type:type
    })
  }
  const toggleMode = ()=>{

    if(mode === 'light'){
      document.body.style.background = 'black';
      document.body.style.transition = 'all 1s'
      setMode('dark')
      localStorage.setItem('mode', 'dark')
      showAlert('Disabled Dark Mode', 'Success')
    }else{
      setMode('light')
      document.body.style.transition = 'all 1s'
      document.body.style.background = 'white';
      localStorage.setItem('mode', 'light')
      setAlert('Enabled Dark Mode', 'Success')
    }
  }

  let animation = {
    transition: 'all 1s',
  }


  return (
  <>

  <Navbar title="Text Utils" mode={mode} toggleMode={toggleMode} animation={animation}/>
  <Alert alert={alert} showAlert={showAlert}/>
  <div className="container my-3">
    <Textform header="Enter the text to analyze below" mode={mode} animation={animation}/>


  </div>
      </>
  );
}

export default App;
