import React, { useState } from 'react'
import PropTypes from 'prop-types'


export default function Textform(props) {
    const handleUpClick = (event)=>{
        setText(text.toUpperCase());
    }
    const handleLoClick = (event)=>{
        setText(text.toLowerCase());
    }
    const handleOnChange = (event)=>{
        setText(event.target.value)
    }
    const handleUpCount = (event) =>{
        let count = 0;
        let wordcnt = 0;
        wordcnt = text.split(' ').length;
        for(let i=0; i<text.length; i++){
            count +=1;

        }
        if(text === ''){
            wordcnt =0;
        }else if(text.endsWith(' ')){
            wordcnt --;
        }
        return [count, wordcnt];
    }
    const [text, setText] = useState('');
    const [count, wordcnt] = handleUpCount();
    return (
        <>
        <div>
            <h5>{props.header}</h5>
        <div className="mb-3">
        <textarea className="form-control" value={text} onChange={handleOnChange} id="exampleFormControlTextarea1" rows="8"></textarea>
        </div>
        <button className="btn btn-primary" onClick={handleUpClick}>Convert to uppercase</button>
        <button className="btn btn-primary mx-2" onClick={handleLoClick}>Convert to lowercase</button>
        <p className='my-2 font'>{count} characters and {wordcnt} words</p>
        <p>Estimated time to read: {(count * 0.003).toFixed(1) < 1 ? 'less than a ': (count*0.003).toFixed(1)} minute</p>
    </div>
    <div>
        <h5>Preview</h5>
        <p>{text}</p>
    </div>
    </>
  )
}

Textform.prototype = {
    header: PropTypes.string.isRequired
}
